mapper.update:
update nc_course
set

    course_name = #{course_name},
    course_notice = #{course_notice},
    course_intro = #{course_intro}

where course_id = #{course_id}

update nc_teacher set
teacher_name = #{teacher_name}, teacher_password = #{teacher_password}, teacher_gender = #{teacher_gender}, teacher_birthday = #{teacher_birthday}, teacher_signature_line = #{teacher_signature_line}
where teacher_id = #{teacher_id}

mapper.select:
select * from nc_record
where record_course_id = #{courseId} 
and (record_deliver_id = #{teacherId} 
or record_receiver_id = #{teacherId})
order by record_time

select * from nc_notice
where notice_course_id = #{courseId}
order by notice_time DESC
